mapper.select:
select
id,
address,
phone,
storeName,
createdAt,
updatedAt,
status
from ctm_store

    id=#{id}
    address like concat('%',#{address},'%')
    phone = #{phone}
    storeName like concat('%',#{storeName},'%')
    createdAt = #{createdAt}
    updatedAt = #{updatedAt}
    status = #{status}

mapper.update:
update ctm_store

    address like concat('%',#{address},'%')
    phone = #{phone}
    storeName like concat('%',#{storeName},'%')
    status = #{status}

where id=#{id}
